CREATE TABLE `boxbot`.`User` ( `id` INT NOT NULL AUTO_INCREMENT , `firstname` VARCHAR(120) NOT NULL , `lastname` VARCHAR(120) NOT NULL , `zipcode` INT NOT NULL , `phonenumber` VARCHAR(120) NOT NULL , `email` VARCHAR(120) NOT NULL , `password` VARCHAR(100) NOT NULL , `is_shopper` BOOLEAN NOT NULL , PRIMARY KEY (`id`), UNIQUE `UNIQUE` (`email`)) ENGINE = InnoDB;
CREATE TABLE `boxbot`.`ProductOrder` ( `id` INT NOT NULL AUTO_INCREMENT , `customer_user_id` INT NOT NULL , `shopper_user_id` INT NOT NULL , `product` VARCHAR(50) NOT NULL , `quantity` INT NOT NULL , `status` VARCHAR(10) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
